import './App.css';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import React, { useState } from 'react'; // Füge useState hinzu
import Header from './components/Header';
import Imprint from './components/Imprint';
import Navigation from './components/Navigation';
import Footer from './components/Footer';
import Service from './components/Service';
import Profile from './components/Profile';
import Lottie from 'lottie-react';
import logoAnimation from './lottie/logoAnimation.json';

function App() {
  const location = useLocation();

  // Zustand und Funktion zum Steuern der Page-Load-Animation hinzufügen
  const [isLoading, setIsLoading] = useState(true); // Standardmäßig true für Animation

  // Funktion zum Deaktivieren der Page-Load-Animation
  const disablePageLoadAnimation = () => {
    setIsLoading(false);
  };

  return (
    <div className="App flex flex-col items-center">
      <Navigation disableAnimation={disablePageLoadAnimation} /> {/* Übergib die Funktion an die Navigation */}
      <TransitionGroup>
        <CSSTransition
          key={location.key}
          timeout={0}
          classNames="black-screen"
        >
          {state => (
            <div className="relative w-full h-full">
              <Routes location={location}>
                <Route
                  path="/"
                  element={
                    <>
                      <Header />
                      <Profile />
                      <Service />
                      <Footer />
                    </>
                  }
                />
                <Route path="/imprint" element={<Imprint />} />
              </Routes>
              {/* Überprüfe den Zustand, um zu entscheiden, ob die Animation angezeigt werden soll */}
              {isLoading && (state === 'entering' || state === 'exiting') ? (
                <div className="fixed inset-0 bg-black flex items-center justify-center z-50">
                  <Lottie animationData={logoAnimation} style={{ height: '200px', width: '200px' }} />
                </div>
              ) : null}
            </div>
          )}
        </CSSTransition>
      </TransitionGroup>
    </div>
  );
}

export default function Root() {
  return (
    <Router>
      <App />
    </Router>
  );
}
