import React, { useEffect, useRef } from 'react';
import './App.css'; // Stelle sicher, dass Tailwind CSS hier eingebunden ist

function App() {
  const cursorRef = useRef(null);
  const cursorFollowerRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (cursorRef.current) {
        cursorRef.current.style.left = `${e.clientX}px`;
        cursorRef.current.style.top = `${e.clientY}px`;
      }
      if (cursorFollowerRef.current) {
        // Verwende eine andere Methode, um die Verzögerung zu simulieren
        cursorFollowerRef.current.style.transform = `translate(${e.clientX}px, ${e.clientY}px)`;
      }
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="relative w-full h-screen bg-gray-100">
      {/* Hauptcursor */}
      <div
        ref={cursorRef}
        className="absolute rounded-full bg-white w-4 h-4 pointer-events-none z-50"
        style={{ transform: 'translate(-50%, -50%)' }}
      />
      {/* Verzögerter Cursor */}
      <div
        ref={cursorFollowerRef}
        className="absolute rounded-full bg-gray-500 w-4 h-4 pointer-events-none z-40 transition-transform duration-500 ease-in-out"
        style={{ transform: 'translate(-50%, -50%)', left: '0', top: '0' }}
      />
      <p className="text-center mt-10">Bewege die Maus, um den Cursor zu sehen.</p>
    </div>
  );
}

export default App;
