import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import i18n from '../i18n'; // Ensure the correct path to your i18n configuration

const LanguageSwitcher = () => {
    const { t } = useTranslation();
    const [language, setLanguage] = useState(i18n.language);

    useEffect(() => {
        setLanguage(i18n.language);
    }, []);

    const changeLanguage = (lng) => {
        i18n.changeLanguage(lng);
        setLanguage(lng);
    };

    const handleToggle = () => {
        changeLanguage(language === 'en' ? 'de' : 'en');
    };

    return (
        <div className="flex items-center">
            <div className="relative inline-block w-16 h-8 mr-2 align-middle select-none transition duration-200 ease-in">
                <input
                    type="checkbox"
                    id="toggle"
                    className="toggle-checkbox absolute block w-full h-full opacity-0 cursor-pointer"
                    checked={language === 'de'}
                    onChange={handleToggle}
                />
                <label
                    htmlFor="toggle"
                    className="toggle-label block overflow-hidden h-8 rounded-full bg-gray-300 cursor-pointer"
                ></label>
                <div className="absolute inset-y-0 left-0 flex items-center justify-center w-8 h-8 text-xs font-bold text-gray-700 transition-transform duration-200 ease-in-out transform toggle-text">
                    {language === 'en' ? 'DE' : 'EN'}
                </div>
            </div>
        </div>
    );
};

export default LanguageSwitcher;
