// Importiere useState von React
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

export const Footer = () => {
    const currentYear = new Date().getFullYear();

    // Zustand, um zu verfolgen, ob der "Back to Top"-Button geklickt wurde
    const [clickedBackToTop, setClickedBackToTop] = useState(false);

    // Funktion, um zum oberen Rand der Seite zu scrollen
    const scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth", // Für sanftes Scrollen
        });
        // Setze den Zustand, dass der Button geklickt wurde
        setClickedBackToTop(true);
    }

    const { t } = useTranslation();

    const footerText = t('footer').split('\n').map((line, index) => (
        <React.Fragment key={index}>
            {line}
            <br />
        </React.Fragment>
    ));

    return (
        <section className="bg-white w-full flex flex-col items-center px-8 md:px-16">
            <div className='mt-24 mb-16 max-w-screen-2xl w-full'>
                <span className='text-[4rem] leading-[4rem] md:text-[8rem] md:leading-[8rem] text-black'>{footerText}</span>
                <div className='mt-8 flex justify-end'>
                    <p className='md:max-w-[387px] uppercase text-base leading-6 text-black font-medium'>
                        {t('outreach')}
                    </p>
                </div>
                <Link to='mailto:info@joschka-kreusser.de' className='text-black border border-black uppercase px-6 py-6 mt-24 flex flex-row justify-center items-center hover:bg-black hover:text-white rounded-full text-2xl gap-2 hover-path-white'>
                    {t('call-to-action')}
                    <svg
                        width="10"
                        height="10"
                        viewBox="0 0 10 10"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                        className='ml-2 h-4 w-4'
                    >
                        <path d="M1 1H9V9" stroke="black" />
                        <path d="M1.15997 8.84L8.99997 1" stroke="black" />
                    </svg>
                </Link>
                <div className='flex flex-col-reverse md:flex-row justify-between items-center mt-48'>
                    <span className='text-black py-2'>
                        &copy;{currentYear} Joschka Kreusser, All Rights Reserved
                    </span>
                    <nav>
                        <ul className='flex flex-row gap-8 items-center'>
                            {/* Füge einen onClick-Handler hinzu, der scrollToTop aufruft */}
                            <li className='text-black flex flex-row items-center justify-start gap-2 py-2' onClick={scrollToTop}>
                                <svg className="stroke-black" width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                                    <path d="M2.05023 7.70712L7.70709 2.05027L13.3639 7.70712" stroke="border-current" />
                                    <path d="M7.70711 13.1377V2.05028" stroke="border-current" />
                                </svg>
                                {t('back-to-top')}
                            </li>
                            <Link to="/imprint">
                                <li className='text-black py-2'>{t('imprint')}</li>
                            </Link>
                            <Link to="/">
                                <li className='text-black py-2'>{t('privacy-policy')}</li>
                            </Link>
                        </ul>
                    </nav>
                </div>
            </div>
        </section>
    );
};

export default Footer;
