        <React.Fragment key={index}>
            {line}
            <br />
        </React.Fragment>
    ));

    return (
        <section className='flex flex-col justify-start max-w-screen-2xl w-full mt-24 mb-32 px-8 md:px-16'>
            <div className='relative max-w-7xl w-full'>
                <span className='text-[5rem] leading-[4rem] md:text-[9.24rem] md:leading-[9.24rem]'>{headerText}</span>
