import React, { useEffect, useRef } from 'react';
import './App.css'; // Stelle sicher, dass Tailwind CSS hier eingebunden ist

function App() {
  const cursorFollowerRef = useRef(null);
  const requestRef = useRef(null);
  const previousPosition = useRef({ x: 0, y: 0 });

  const updateCursorPosition = (e) => {
    if (cursorFollowerRef.current) {
      // Speichern der aktuellen Position
      previousPosition.current = { x: e.clientX, y: e.clientY };
    }
  };

  const animateCursor = () => {
    if (cursorFollowerRef.current) {
      // Berechnen der aktuellen Position mit einer Verzögerung
      const { x, y } = previousPosition.current;
      cursorFollowerRef.current.style.transform = `translate(${x}px, ${y}px)`;
    }
    requestRef.current = requestAnimationFrame(animateCursor);
  };

  useEffect(() => {
    document.addEventListener('mousemove', updateCursorPosition);

    requestRef.current = requestAnimationFrame(animateCursor);

    return () => {
      document.removeEventListener('mousemove', updateCursorPosition);
      cancelAnimationFrame(requestRef.current);
    };
  }, []);

  return (
    <div className="relative w-full h-screen bg-gray-100">
      {/* Verzögerter grauer Cursor */}
      <div
        ref={cursorFollowerRef}
        className="absolute rounded-full bg-gray-500 w-4 h-4 pointer-events-none z-50"
        style={{
          transform: 'translate(-50%, -50%)',
          transition: 'transform 0.3s ease-in-out'
        }}
      />
      <p className="text-center mt-10">Bewege die Maus, um den Cursor zu sehen.</p>
    </div>
  );
}

export default App;
