import './App.css';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import Header from './components/Header';
import Imprint from './components/Imprint';
import Navigation from './components/Navigation';
import Footer from './components/Footer';
import Service from './components/Service';
import Profile from './components/Profile';
import Work from './components/Work';
import Process from './components/Process';
import Lottie from 'lottie-react';
import logoAnimation from './lottie/logoAnimation.json';
import { useState, useEffect } from 'react';
import useScrollEffect from './useScrollEffect';
import { animated } from '@react-spring/web';

function App() {
  const location = useLocation();
  const [firstLoad, setFirstLoad] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setFirstLoad(false);
    }, 1000); // 1 Sekunde warten, bevor firstLoad auf false gesetzt wird
    return () => clearTimeout(timer);
  }, []);

  const scrollProps = useScrollEffect(); // Verwende den Scroll-Effekt Hook

  return (
    <div className="App flex flex-col items-center w-full">
      <Navigation />
      <TransitionGroup className="w-full">
        <CSSTransition
          key={location.key}
          timeout={1100}
          classNames="black-screen"
        >
          {state => (
            <animated.div className="relative w-full h-full" style={scrollProps}>
              <Routes location={location} className="w-full">
                <Route
                  path="/"
                  element={
                    <>
                      <Header />
                      <Work />
                      <Process />
                      <Profile />
                      <Service />
                      <section className='w-full bg-white'>
                        <Footer />
                      </section>
                    </>
                  }
                />
                <Route
                  path="/imprint" element={
                    <>
                      <Imprint />
                    </>
                  }
                />
              </Routes>
              {(firstLoad || state === 'entering' || state === 'exiting') && (
                <div className="fixed inset-0 bg-black flex items-center justify-center z-50">
                  <Lottie animationData={logoAnimation} style={{ height: '200px', width: '200px' }} />
                </div>
              )}
            </animated.div>
          )}
        </CSSTransition>
      </TransitionGroup>
    </div>
  );
}

export default function Root() {
  return (
    <Router>
      <App />
    </Router>
  );
}
