import React from 'react';
import { useTranslation } from 'react-i18next';

const Imprint = () => {
    const { t } = useTranslation();

    const adressText = t('adress').split('\n').map((line, index) => (
        <React.Fragment key={index}>
            {line}
            <br />
        </React.Fragment>
    ));

    return (
        <section className="flex flex-col justify-start max-w-screen-2xl mt-24 mb-32 w-full px-8 md:px-16">
            <h2 className='mb-8 text-4xl w-full'>{t('imprint')}</h2>
            <hr className='border-white/10 w-full mb-16'></hr>
            <div className="mb-4">
                <h3 className="text-lg font-semibold mb-2">{t('imprint-heading-1')}</h3>
                <p>{adressText}</p>
            </div>
            <div className="mb-4">
                <h3 className="text-lg font-semibold mb-2">{t('contact')}</h3>
                <p>Email: {t('e-mail')}</p>
            </div>
            <div className="mb-4">
                <h3 className="text-lg font-semibold mb-2">{t('vat-id-heading')}</h3>
                <p>{t('vat-id-paragraph')}</p>
            </div>
            <div className="mb-4 !w-full">
                <h3 className="text-lg font-semibold mb-2">EU Dispute Resolution:</h3>
                <p className='max-w-2xl'>The European Commission provides a platform for online dispute resolution (ODR): <a href="https://ec.europa.eu/consumers/odr/">https://ec.europa.eu/consumers/odr/</a>. You can find our email address above in the imprint.</p>
            </div>
            <div className="mb-4 !w-full">
                <h3 className="text-lg font-semibold mb-2">Consumer Dispute Resolution/Universal Arbitration Board:</h3>
                <p className='max-w-2xl'>We participate in dispute resolution proceedings before a consumer arbitration board. The competent authority is the Universal Arbitration Board of the Center for Arbitration e.V., Straßburger Straße 8, 77694 Kehl am Rhein (<a href="https://www.verbraucher-schlichter.de">https://www.verbraucher-schlichter.de</a>).</p>
            </div>
        </section>
    );
}

export default Imprint;
