import React, { useEffect, useRef } from 'react';
import './App.css'; // Stelle sicher, dass Tailwind CSS hier eingebunden ist

function App() {
  const cursorFollowerRef = useRef(null);

  useEffect(() => {
    const handleMouseMove = (e) => {
      if (cursorFollowerRef.current) {
        cursorFollowerRef.current.style.left = `${e.clientX}px`;
        cursorFollowerRef.current.style.top = `${e.clientY}px`;
      }
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <div className="relative w-full h-screen bg-gray-100">
      {/* Verz√∂gerter grauer Cursor */}
      <div
        ref={cursorFollowerRef}
        className="absolute rounded-full bg-gray-500 w-4 h-4 pointer-events-none z-50 transition-transform duration-300 ease-in-out"
        style={{ transform: 'translate(-50%, -50%)' }}
      />
      <p className="text-center mt-10">Bewege die Maus, um den Cursor zu sehen.</p>
    </div>
  );
}

export default App;
