import React, { useState, useEffect } from 'react';
import i18n from '../i18n';

const LanguageSwitcher = () => {
    const [language, setLanguage] = useState(i18n.language);

    useEffect(() => {
        setLanguage(i18n.language);
    }, []);

    const handleToggle = () => {
        const newLanguage = language === 'en' ? 'de' : 'en';
        i18n.changeLanguage(newLanguage);
        setLanguage(newLanguage);
    };

    return (
        <div className="flex items-center">
            <div
                className="relative inline-block w-20 h-10 mr-2 align-middle select-none transition duration-200 ease-in"
                onClick={handleToggle}
            >
                <div className="block overflow-hidden h-10 rounded-full bg-gray-300 cursor-pointer"></div>
                <div className={`absolute top-0 left-0 w-10 h-10 rounded-full bg-white border-4 transition-transform duration-200 ease-in-out transform ${language === 'de' ? 'translate-x-full' : ''}`}>
                    <div className="flex items-center justify-center w-full h-full text-xs font-bold text-gray-700">
                        {language === 'en' ? 'DE' : 'EN'}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LanguageSwitcher;
