import './App.css';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { useState } from 'react'; // Import useState hook
import Header from './components/Header';
import Imprint from './components/Imprint';
import Navigation from './components/Navigation';
import Footer from './components/Footer';
import Service from './components/Service';
import Profile from './components/Profile';
import Lottie from 'lottie-react';
import logoAnimation from './lottie/logoAnimation.json';

function App() {
  const location = useLocation();
  const [playAnimation, setPlayAnimation] = useState(false); // State to control animation playback

  const handlePageLoad = () => {
    setPlayAnimation(true); // Set playAnimation state to true when Impressum link is clicked
  };

  return (
    <div className="App flex flex-col items-center">
      <Navigation />
      <TransitionGroup>
        <CSSTransition
          key={location.key}
          timeout={1100} // Adjusted to fit animation duration
          classNames="black-screen"
        >
          {state => (
            <div className="relative w-full h-full">
              <Routes location={location}>
                <Route
                  path="/"
                  element={
                    <>
                      <Header />
                      <Profile />
                      <Service />
                      <Footer pageLoad={handlePageLoad} /> {/* Pass handleImprintLinkClick function to Footer */}
                    </>
                  }
                />
                <Route path="/imprint" element={<Imprint />} />
              </Routes>
              {playAnimation && (
                <div className="fixed inset-0 bg-black flex items-center justify-center z-50">
                  <Lottie animationData={logoAnimation} style={{ height: '200px', width: '200px' }} />
                </div>
              )}
            </div>
          )}
        </CSSTransition>
      </TransitionGroup>
    </div>
  );
}

export default function Root() {
  return (
    <Router>
      <App />
    </Router>
  );
}
