import React, { useEffect, useRef, useState } from "react";

function Cursor() {
    const cursorDot = useRef(null);
    const cursorDotOutline = useRef(null);
    const requestRef = useRef();
    const previousTimeRef = useRef();
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

    useEffect(() => {
        const onMouseMove = (e) => {
            setMousePosition({ x: e.pageX, y: e.pageY });
        };

        const animateDotOutline = (time) => {
            if (previousTimeRef.current !== undefined) {
                const { x, y } = mousePosition;
                if (cursorDotOutline.current) {
                    cursorDotOutline.current.style.transform = `translate(${x}px, ${y}px)`;
                }
                if (cursorDot.current) {
                    cursorDot.current.style.transform = `translate(${x}px, ${y}px)`;
                }
            }
            previousTimeRef.current = time;
            requestRef.current = requestAnimationFrame(animateDotOutline);
        };

        document.addEventListener("mousemove", onMouseMove);

        requestRef.current = requestAnimationFrame(animateDotOutline);

        return () => {
            document.removeEventListener("mousemove", onMouseMove);
            cancelAnimationFrame(requestRef.current);
        };
    }, [mousePosition]);

    return (
        <>
            <div
                ref={cursorDotOutline}
                className="absolute rounded-full border-2 border-gray-500 w-10 h-10 pointer-events-none z-50"
                style={{ transform: 'translate(-50%, -50%)' }}
            />
            <div
                ref={cursorDot}
                className="absolute rounded-full bg-gray-500 w-6 h-6 pointer-events-none z-50"
                style={{ transform: 'translate(-50%, -50%)' }}
            />
        </>
    );
}

export default Cursor;
