import React from 'react';
import { useTranslation } from 'react-i18next';

const Profile = () => {
    const { t } = useTranslation();

    return (
        <section id="profile" className="max-w-screen-2xl w-full mx-auto flex flex-col items-start justify-center text-left mt-32 mb-48 px-8 md:px-16">
            <h2 className='mb-8 text-4xl'>{t('profile')}</h2>
            <hr className='border-white/10 w-full'></hr>
            <div className='mt-24 w-full flex flex-col'>
                <p className='text-6xl'>Crafting visual experiences for digital-first brands.</p>
                <p className="text-4xl font-extralight max-w-4xl items-end">
                    I&apos;m Joschka Kreusser, a digital designer based in Munich. I&apos;m a generalist by design always up for new challenges, well versed in taking a vision and helping turn that into a living breathing entity. I love my work and that&apos;s why I always strive for the best results. If it doesn&apos;t meet the highest standards it won&apos;t leave my desk.
                </p>

            </div>
        </section>
    );
}

export default Profile;
