import React, { useState, useEffect, useRef } from "react";
import '../App.css'; // Sicherstellen, dass CSS eingebunden ist

function Cursor() {
    const cursorDot = useRef(null);
    const previousPositionRef = useRef({ x: 0, y: 0 });
    const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
    const [cursorVisible, setCursorVisible] = useState(true);
    const [cursorEnlarged, setCursorEnlarged] = useState(false);

    useEffect(() => {
        const onMouseMove = (event) => {
            setMousePosition({ x: event.clientX, y: event.clientY });
        };

        const onMouseEnter = () => setCursorVisible(true);
        const onMouseLeave = () => setCursorVisible(false);
        const onMouseDown = () => setCursorEnlarged(true);
        const onMouseUp = () => setCursorEnlarged(false);

        const animateCursor = () => {
            const { x, y } = mousePosition;
            if (cursorDot.current) {
                previousPositionRef.current.x += (x - previousPositionRef.current.x) / 4; // Verringerung der Verzögerung
                previousPositionRef.current.y += (y - previousPositionRef.current.y) / 4; // Verringerung der Verzögerung
                cursorDot.current.style.transform = `translate(${previousPositionRef.current.x}px, ${previousPositionRef.current.y}px)`;
            }
            requestAnimationFrame(animateCursor);
        };

        document.addEventListener("mousemove", onMouseMove);
        document.addEventListener("mouseenter", onMouseEnter);
        document.addEventListener("mouseleave", onMouseLeave);
        document.addEventListener("mousedown", onMouseDown);
        document.addEventListener("mouseup", onMouseUp);

        requestAnimationFrame(animateCursor);

        return () => {
            document.removeEventListener("mousemove", onMouseMove);
            document.removeEventListener("mouseenter", onMouseEnter);
            document.removeEventListener("mouseleave", onMouseLeave);
            document.removeEventListener("mousedown", onMouseDown);
            document.removeEventListener("mouseup", onMouseUp);
        };
    }, [mousePosition]);

    useEffect(() => {
        if (cursorDot.current) {
            cursorDot.current.style.opacity = cursorVisible ? 1 : 0;
        }
    }, [cursorVisible]);

    useEffect(() => {
        if (cursorDot.current) {
            cursorDot.current.style.transform = cursorEnlarged
                ? "translate(-50%, -50%) scale(1.5)"
                : "translate(-50%, -50%) scale(1)";
        }
    }, [cursorEnlarged]);

    return (
        <div ref={cursorDot} className="cursor-dot" />
    );
}

export default Cursor;
