import React from 'react';
import { Link } from 'react-router-dom';

export const Footer = () => {
    const currentYear = new Date().getFullYear();

    // Function to scroll to the top of the page
    const scrollToTop = () => {
        window.scrollTo({
            top: 0,
            behavior: "smooth", // For smooth scrolling
        });
    };

    return (
        <section className="bg-pk-black text-pk-white py-20 w-screen relative left-1/2 -translate-x-1/2">
            <div className="mx-auto max-w-screen-xl h-full w-full px-8 items-center">
                <div className="flex flex-col md:flex-row md:justify-between items-center">
                    <Link href="/" className="flex items-center gap-2 mb-2 md:mb-0 py-3 mr-8">
                        <svg width="38" height="32" viewBox="0 0 38 32" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M23.9203 0H13.2891V32H23.9203V0Z" fill="white" />
                            <path d="M26.5781 10.6667V0H37.2093L26.5781 10.6667Z" fill="white" />
                            <path d="M37.2093 32H26.5781V21.3333L37.2093 32Z" fill="white" />
                            <path d="M5.31561 21.3333C8.24983 21.3333 10.6312 23.7227 10.6312 26.6667V32H0V26.6667C0 23.7227 2.3814 21.3333 5.31561 21.3333Z" fill="white" />
                        </svg>
                        <span className="text-2xl font-bold text-pk-white">PersonaKit</span>
                    </Link>
                    <div className="flex py-3 md:w-auto my-1">
                        <div className="flex md:items-center gap-4 mb-2 md:mb-0">
                            <div className="flex flex-col md:flex-row gap-8 justify-center items-center md:justify-start">
                                <Link href="#produkt">Produkt</Link>
                                <Link href="#leistungen">Leistungen</Link>
                                <Link href="#preise">Preise</Link>
                                <Link href="#faq">FAQ</Link>
                                <Link href="#kontakt">Kontakt</Link>
                            </div>
                        </div>
                    </div>
                </div>
                <hr className="border-pk-white border mt-20 mb-8" />
                <div className="flex flex-col md:flex-row justify-between text-sm md:text-base items-center">
                    <span className="pb-6 mb:pb-0">© {currentYear} PersonaKit</span>
                    <div className="flex flex-col md:flex-row gap-8 items-center">
                        <Link href="/" className="flex flex-row gap-2 items-center" onClick={scrollToTop}>
                            <ArrowUp className="w-4 h-4" />
                            Zurück zum Anfang
                        </Link>
                        <Link href="/datenschutz">Datenschutz</Link>
                        <Link href="/impressum">Impressum</Link>
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Footer;
