import React from 'react';
import Lottie from 'lottie-react';
import strategyAnimation from '../lottie/strategy_animation.json';
import designAnimation from '../lottie/design_animation.json';
import developmentAnimation from '../lottie/development_animation.json';

const Impressum = () => {
    return (
        <section className="max-w-7xl mx-auto p-4 flex flex-col items-start justify-center text-left my-32">
            <h2 className='mb-8 text-4xl'>Service</h2>
            <hr className='border-white/10 w-full'></hr>
            <div className="flex flex-row w-full justify-between items-stretch gap-8">
                <div className="flex flex-col gap-16 py-24">
                    <Lottie
                        animationData={strategyAnimation}
                        loop={true}
                        autoplay={true}
                        style={{ height: 200, width: 200 }}
                    />
                    <span className="uppercase text-base leading-6">1 Strategy</span>
                    <ul className='text-white/50'>
                        <li>Creative Lead</li>
                        <li>Research</li>
                        <li>Competitor Analysis</li>
                        <li>User Journeys / Flows</li>
                        <li>Mind Mapping</li>
                        <li>Personas</li>
                    </ul>
                </div>
                <div className="w-[1px] bg-white/10"></div>
                <div className="flex flex-col gap-16 py-24">
                    <Lottie
                        animationData={designAnimation}
                        loop={true}
                        autoplay={true}
                        style={{ height: 200, width: 200 }}
                    />
                    <span className="uppercase text-base leading-6">2 Design</span>
                    <ul>
                        <li>User Experience (UX)</li>
                        <li>User Interface (UI)</li>
                        <li>Brand Design</li>
                        <li>Motion Design</li>
                        <li>Visual Communication</li>
                        <li>Prototypes</li>
                    </ul>
                </div>
                <div className="w-[1px] bg-white/10"></div>
                <div className="flex flex-col gap-16 py-24">
                    <Lottie
                        animationData={developmentAnimation}
                        loop={true}
                        autoplay={true}
                        style={{ height: 200, width: 200 }}
                    />
                    <span className="uppercase text-base leading-6">3 Development</span>
                    <ul>
                        <li>Front End Development</li>
                        <li>Content Management Systems</li>
                        <li>Version Management</li>
                        <li>eCommerce</li>
                    </ul>
                </div>
            </div>
        </section>
    );
}

export default Impressum;
