import React, { createContext, useContext, useEffect, useRef, useState } from 'react';

// Custom Hook für Mausposition
function useMousePosition() {
    const [mousePosition, setMousePosition] = useState({ x: null, y: null });

    useEffect(() => {
        const mouseMoveHandler = (event) => {
            const { clientX, clientY } = event;
            setMousePosition({ x: clientX, y: clientY });
        };
        document.addEventListener("mousemove", mouseMoveHandler);

        return () => {
            document.removeEventListener("mousemove", mouseMoveHandler);
        };
    }, []);

    return mousePosition;
}

// Context für Cursor-Typ
export const MouseContext = createContext({
    cursorType: "",
    cursorChangeHandler: () => { },
});

const MouseContextProvider = ({ children }) => {
    const [cursorType, setCursorType] = useState("");

    const cursorChangeHandler = (cursorType) => {
        setCursorType(cursorType);
    };

    return (
        <MouseContext.Provider value={{ cursorType, cursorChangeHandler }}>
            {children}
        </MouseContext.Provider>
    );
};

// Cursor-Komponente
function Cursor() {
    const { cursorType } = useContext(MouseContext);
    const cursorDotOutline = useRef(null);
    const cursorDot = useRef(null);
    const mousePosition = useMousePosition();
    const requestRef = useRef();
    const previousPositionRef = useRef({ x: 0, y: 0 });

    useEffect(() => {
        const animateCursor = () => {
            if (cursorDotOutline.current && cursorDot.current) {
                const { x, y } = mousePosition;
                if (x !== null && y !== null) {
                    // Verzögerte Position für den äußeren Cursor
                    previousPositionRef.current.x += (x - previousPositionRef.current.x) / 8;
                    previousPositionRef.current.y += (y - previousPositionRef.current.y) / 8;

                    cursorDotOutline.current.style.transform = `translate(${previousPositionRef.current.x}px, ${previousPositionRef.current.y}px)`;
                    cursorDot.current.style.transform = `translate(${x}px, ${y}px)`;
                }
            }

            requestRef.current = requestAnimationFrame(animateCursor);
        };

        requestRef.current = requestAnimationFrame(animateCursor);

        return () => {
            cancelAnimationFrame(requestRef.current);
        };
    }, [mousePosition]);

    return (
        <>
            <div
                ref={cursorDotOutline}
                className={`cursor-dot-outline ${cursorType === 'outline' ? 'cursor-outline-active' : ''}`}
            />
            <div
                ref={cursorDot}
                className={`cursor-dot ${cursorType === 'dot' ? 'cursor-dot-active' : ''}`}
            />
            <style jsx>{`
        /* Stile für den verzögerten Cursor */
        .cursor-dot-outline {
          position: absolute;
          width: 16px;
          height: 16px;
          border-radius: 50%;
          border: 2px solid gray;
          background-color: transparent;
          pointer-events: none;
          transform: translate(-50%, -50%);
          z-index: 9999;
          transition: transform 0.3s cubic-bezier(0.25, 0.1, 0.25, 1);
        }

        .cursor-dot {
          position: absolute;
          width: 8px;
          height: 8px;
          border-radius: 50%;
          background-color: gray;
          pointer-events: none;
          transform: translate(-50%, -50%);
          z-index: 10000;
        }

        .cursor-outline-active {
          /* Zusätzliche Stile für den äußeren Cursor, wenn aktiv */
        }

        .cursor-dot-active {
          /* Zusätzliche Stile für den normalen Cursor, wenn aktiv */
        }
      `}</style>
        </>
    );
}

// Export der Komponenten
export default Cursor;
export { MouseContextProvider };
