import { useTranslation } from 'react-i18next';

const Header = () => {
    const { t } = useTranslation();

    return (
        <section className='flex justify-start max-w-7xl'>
            <div className='relative max-w-6xl w-full mt-24'>
                <span className='text-[4rem] leading-[3rem] md:text-[8rem] md:leading-[8rem] max-w-5xl'>{t('header')}</span>
                <div className='flex justify-end uppercase text-base'>
                    <p>Specializing in the development of digital <br></br>products such as website and mobile app <br></br>design, brand identity, UI/UX, interaction <br></br>design, and front-end development.</p>
                </div>
            </div>
        </section >
    );
}

export default Header;
