import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import i18n from '../i18n'; // Ensure the correct path to your i18n configuration

const LanguageSwitcher = () => {
    const { t } = useTranslation();
    const [language, setLanguage] = useState(i18n.language);

    useEffect(() => {
        setLanguage(i18n.language);
    }, []);

    const handleToggle = () => {
        const newLanguage = language === 'en' ? 'de' : 'en';
        i18n.changeLanguage(newLanguage);
        setLanguage(newLanguage);
    };

    return (
        <div className="flex items-center">
            <div
                className="relative inline-block w-16 h-8 mr-2 align-middle select-none transition duration-200 ease-in"
                onClick={handleToggle}
            >
                <input
                    type="checkbox"
                    id="toggle"
                    className="sr-only"
                    checked={language === 'de'}
                    onChange={handleToggle}
                />
                <label
                    htmlFor="toggle"
                    className="block overflow-hidden h-8 rounded-full bg-gray-300 cursor-pointer"
                ></label>
                <div className={`absolute top-0 left-0 w-8 h-8 rounded-full bg-white border-4 transition-transform duration-200 ease-in-out transform ${language === 'de' ? 'translate-x-full' : ''}`}>
                    <div className="flex items-center justify-center w-full h-full text-xs font-bold text-gray-700">
                        {language === 'en' ? 'DE' : 'EN'}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default LanguageSwitcher;
