import React from 'react';

const navItems = [
    { name: 'About', href: '#about' },
    { name: 'Service', href: '#service' },
    { name: 'Work', href: '#work' },
    { name: 'Contact', href: '#contact', hasIcon: true },
];

const Navigation = () => {
    return (
        <nav className='max-w-3xl mx-auto p-4'>
            <ul className='flex flex-row gap-8'>
                {navItems.map((item, index) => (
                    <li
                        key={index}
                        className={`uppercase px-8 py-2 flex flex-col items-center rounded-3xl ${item.hasIcon ? 'bg-white/10 text-white' : ''
                            }`}
                    >
                        <a href={item.href} className='flex items-center'>
                            {item.name}
                            {item.hasIcon && (
                                <svg
                                    width="10"
                                    height="10"
                                    viewBox="0 0 10 10"
                                    fill="none"
                                    xmlns="http://www.w3.org/2000/svg"
                                    className='ml-2'
                                >
                                    <path d="M1 1H9V9" stroke="white" />
                                    <path d="M1.15997 8.84L8.99997 1" stroke="white" />
                                </svg>
                            )}
                        </a>
                    </li>
                ))}
            </ul>
        </nav>
    );
};

export default Navigation;
