import React from 'react';
import { useTranslation } from 'react-i18next';

const PersonaKit = () => {
    const { t } = useTranslation();

    const adressText = t('adress').split('\n').map((line, index) => (
        <React.Fragment key={index}>
            {line}
            <br />
        </React.Fragment>
    ));

    return (
        <section className="max-w-screen-2xl mx-auto flex flex-col justify-center mt-24 mb-32 px-8 md:px-16 border-black">
            <h2 className='mb-8 text-4xl w-full'>{t('imprint')}</h2>
            <hr className='border-white/10 w-full mb-16'></hr>
            <div className='flex flex-col gap-8'>
                <div>
                    <h3 className="text-lg font-semibold mb-2">{t('information-heading')}</h3>
                    <p>{adressText}</p>
                </div>
                <div>
                    <h3 className="text-lg font-semibold mb-2">{t('contact')}</h3>
                    <p>Email: {t('e-mail')}</p>
                </div>
                <div>
                    <h3 className="text-lg font-semibold mb-2">{t('vat-id-heading')}</h3>
                    <p>{t('vat-id-paragraph')}</p>
                </div>
                <div>
                    <h3 className="text-lg font-semibold mb-2">{t('eu-resolution-heading')}</h3>
                    <p className='max-w-2xl'>{t('eu-resolution-praragraph-1')} <a href="https://ec.europa.eu/consumers/odr/">https://ec.europa.eu/consumers/odr/</a>. {t('eu-resolution-praragraph-2')}</p>
                </div>
                <div>
                    <h3 className="text-lg font-semibold mb-2">{t('consumer-resolution-heading')}</h3>
                    <p className='max-w-2xl'>{t('consumer-resolution-paragraph')} (<a href="https://www.verbraucher-schlichter.de">https://www.verbraucher-schlichter.de</a>).</p>
                </div>
            </div>
        </section>
    );
}

export default PersonaKit;
