import React from 'react';
import Lottie from 'lottie-react';
import strategyAnimation from '../lottie/strategy_animation.json';
import designAnimation from '../lottie/design_animation.json';
import developmentAnimation from '../lottie/development_animation.json';
import { useTranslation } from 'react-i18next';

const Process = () => {
    const { t } = useTranslation();

    return (
        <section id="process" className="max-w-screen-2xl mx-auto flex flex-col items-start justify-center text-left my-32 md:my-48 px-8 md:px-16">
            <h2 className='mb-8 text-4xl'>{t('process')}</h2>
            <hr className='border-white/10 w-full'></hr>
            <div className="flex flex-col md:flex-row w-full justify-between items-stretch md:gap-8 lg:gap-24">
                <div className="flex flex-col items-start gap-16 py-24 flex-1">
                    <div className='w-full flex justify-center'>
                        <Lottie
                            animationData={strategyAnimation}
                            loop={true}
                            autoplay={true}
                            style={{ height: 'auto', width: '80%' }}
                        />
                    </div>
                    <span className="uppercase text-2xl md:text-base md:leading-6">1 Discussion</span>
                    <p className='text-white/50 max-w-64'>Understand the client's business, goals, and challenges to lay the foundation for the project.</p>
                </div>
                <div className="w-[1px] bg-white/10 hidden md:block"></div>
                <div className="h-[1px] bg-white/10 md:hidden"></div>
                <div className="flex flex-col items-start gap-16 py-24 flex-1">
                    <div className='w-full flex justify-center'>
                        <Lottie
                            animationData={designAnimation}
                            loop={true}
                            autoplay={true}
                            style={{ height: 'auto', width: '80%' }}
                        />
                    </div>
                    <span className="uppercase text-2xl md:text-base md:leading-6">2 Ideation</span>
                    <p className='text-white/50'>Generate and refine concepts that align with the client's vision and audience needs.</p>
                </div>
                <div className="w-[1px] bg-white/10 hidden md:block"></div>
                <div className="h-[1px] bg-white/10 md:hidden"></div>
                <div className="flex flex-col items-start gap-16 py-24 flex-1">
                    <div className='w-full flex justify-center'>
                        <Lottie
                            animationData={developmentAnimation}
                            loop={true}
                            autoplay={true}
                            style={{ height: 'auto', width: '80%' }}
                        />
                    </div>
                    <span className="uppercase text-2xl md:text-base md:leading-6">3 Execution</span>
                    <p className='text-white/50'>Bring the concept to life through design and development, ensuring quality and effectiveness.</p>
                </div>
            </div>
        </section>
    );
}

export default Process;
