import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import Confetti from 'react-dom-confetti';

const config = {
    angle: 90,
    spread: 360,
    startVelocity: 20,
    elementCount: 70,
    dragFriction: 0.12,
    duration: 3000,
    stagger: 3,
    width: "10px",
    height: "10px",
    perspective: "500px",
    colors: ["#a864fd", "#29cdff", "#78ff44", "#ff718d", "#fdff6a"]
};

const Header = () => {
    const { t } = useTranslation();
    const [active, setActive] = useState(false);

    const handleClick = () => {
        setActive(true);
        setTimeout(() => setActive(false), 3000); // Reset after 3 seconds
    };

    return (
        <div className='max-w-7xl w-full mt-24'>
            <span className='header-text max-w-5xl'>{t('header')}</span>
            <div className='confetti-button flex justify-center items-center w-24 h-24 bg-white/10 rounded-full' onClick={handleClick}>
                <svg width="39" height="39" viewBox="0 0 39 39" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M1 2H37V38" stroke="white" stroke-width="3" />
                    <path d="M1.72015 37.28L37.0002 2" stroke="white" stroke-width="3" />
                </svg>
                <Confetti active={active} config={config} />
            </div>
        </div>
    );
}

export default Header;
