import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import Maintenance from './components/Maintenance';
import Imprint from './components/Imprint';
import Impressum from './components/Impressum';

function App() {
  const location = useLocation();

  // Function to determine whether to show the header
  const showHeader = () => {
    return !['/imprint', '/impressum'].includes(location.pathname);
  };

  return (
    <Router>
      <div className="App flex flex-col items-center justify-center">
        {showHeader() && (
          <header className="App-header max-w-3xl flex justify-between items-center w-full p-4 absolute top-0">
            <div></div>
            <nav className="flex items-center">
              <Link className="text-base" to="/imprint">Imprint</Link>
            </nav>
          </header>
        )}
        <div>
          <Routes>
            <Route path="/" element={<Maintenance />} />
            <Route path="/imprint" element={<Imprint />} />
            <Route path="/impressum" element={<Impressum />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
