import React from 'react';
import { useTranslation } from 'react-i18next';

const Work = () => {
    const { t } = useTranslation();

    return (
        <section id="work" className="max-w-screen-2xl w-full mx-auto flex flex-col items-start justify-center text-left my-16 md:my-32 px-8 md:px-16">
            <h2 className='mb-8 text-4xl'>{t('work')}</h2>
            <hr className='border-white/10 w-full'></hr>
            <div className="link-container">
                <a href="#personaKit" className="link-text text-4xl lg:text-6xl font-extralight">
                    PersonaKit - Web App Design & Development
                </a>
            </div>
            <hr className='border-white/10 w-full'></hr>
            <div className="link-container">
                <a href="#cancom" className="link-text text-4xl lg:text-6xl font-extralight">
                    Cancom - Digital Storytelling & Motion Design
                </a>
            </div>
            <hr className='border-white/10 w-full'></hr>
            <div className="link-container">
                <a href="#shapeUp" className="link-text text-4xl lg:text-6xl font-extralight">
                    Shape Up - App Design
                </a>
            </div>
            <hr className='border-white/10 w-full'></hr>
        </section>
    );
}

export default Work;
