import './App.css';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import Header from './components/Header';
import Imprint from './components/Imprint';
import PrivacyPolicy from './components/PrivacyPolicy';
import Navigation from './components/Navigation';
import Footer from './components/Footer';
import Service from './components/Service';
import Profile from './components/Profile';
import Work from './components/Work';
import Process from './components/Process';
import PersonaKit from './work/PersonaKit';
import DentaltechnikWeber from './work/DentaltechnikWeber';
import ShapeUp from './work/ShapeUp';
import Lottie from 'lottie-react';
import logoAnimation from './lottie/logoAnimation.json';
import { useState, useEffect, useRef } from 'react';
import { Helmet, HelmetProvider } from 'react-helmet-async';
import { useTranslation } from 'react-i18next';

function App() {
  const { t } = useTranslation();
  const location = useLocation();
  const [firstLoad, setFirstLoad] = useState(true);
  const [showAnimation, setShowAnimation] = useState(true);
  const prevPathname = useRef(location.pathname);

  // Initiale Animation nur beim ersten Laden der Seite
  useEffect(() => {
    const timer = setTimeout(() => setFirstLoad(false), 1000);
    return () => clearTimeout(timer);
  }, []); // Leer, damit dieser Effekt nur einmal beim initialen Laden ausgeführt wird

  useEffect(() => {
    const isPathnameChanged = prevPathname.current !== location.pathname;
    const isHashChange = location.hash !== ''; // Überprüfen, ob ein Hash vorhanden ist

    // Nur Animation zeigen, wenn sich der Pfad (pathname) geändert hat oder es der erste Ladevorgang ist
    if (isPathnameChanged && !isHashChange) {
      setShowAnimation(true);
      const timer = setTimeout(() => setShowAnimation(false), 1000); // Dauer der Animation

      return () => clearTimeout(timer);
    } else {
      // Animation nicht anzeigen, wenn nur der Hash sich ändert
      setShowAnimation(false);
    }

    // Scrollen zur ID, falls ein Hash vorhanden ist
    if (location.hash) {
      const element = document.getElementById(location.hash.slice(1));
      if (element) {
        element.scrollIntoView({ behavior: 'smooth', block: 'start' });
      }
    } else {
      // Wenn kein Hash vorhanden ist, scrollt die Seite zum Anfang
      window.scrollTo(0, 0);
    }

    // Aktualisieren des vorherigen Pfads für den nächsten Vergleich
    prevPathname.current = location.pathname;
  }, [location, firstLoad]); // `location` und `firstLoad` als Abhängigkeiten

  return (
    <div className="App flex flex-col items-center w-full">
      <Navigation />
      <TransitionGroup className="w-full">
        <CSSTransition
          key={location.key}
          timeout={1100}
          classNames="black-screen"
        >
          {state => (
            <div className="relative w-full h-full">
              <Routes location={location} className="w-full">
                <Route
                  path="/"
                  element={
                    <>
                      <Helmet>
                        <title>{t('home-seo-title')}</title>
                        <meta name="description" content={t('home-seo-description')} />
                        <meta property="og:title" content={t('home-seo-title')} />
